import React from "react";
import Map from './Map'

const Modal = ({state, dispatch, coordinates}) => {
  return (
    <div className={`modal ${state && 'is-active'}`}>
      <div className="modal-background" onClick={()=>dispatch({type: 'MAP'})}></div>
      <div className="modal-content">
      {coordinates ? <Map center={coordinates} zoom={16} /> : 
      <article className="message is-danger">
      <div className="message-header">
        <p>Delete Place</p>
        <button className="delete" aria-label="delete"></button>
      </div>
      <div className="message-body">
        Are you sure do you want to delete?
      </div>
    </article>
      }

      </div>
      <button className="modal-close is-large" aria-label="close" onClick={()=>dispatch({type: 'MAP'})} ></button>
    </div>
  );
};

export default Modal;
