import React from "react";
import Layout from "../../components/Layout";
import Input from "../../components/Input";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from "../../utils/validators";
import { useRouter } from "next/router";
import { places } from "../../data";
import { useForm } from "../../hooks/useForm";

const editPlace = () => {
  const router = useRouter();
  const findPlace = places.find((place) => place.id === router.query.editPlace);

  const {title, description, address} = findPlace
  const [formState, inputHandler] = useForm(
    {
      title: {
        value: title,
        isValid: true,
      },
      description: {
        value: description,
        isValid: true,
      },
      address: {
        value: address,
        isValid: true,
      },
    },
    true
  );


  return (
    <Layout title="Edit Place" subtitle="form to update an existing place">
      {findPlace ? (
        <>
          <Input
            id="title"
            name="Title"
            placeholder="Name of the place"
            icon="fa-landmark"
            onInput={inputHandler}
            validators={[VALIDATOR_REQUIRE()]}
            value={formState.title.value}
            valid={formState.title.isValid}
          />
          <Input
            id="description"
            name="Description"
            type="textarea"
            placeholder="About the place"
            onInput={inputHandler}
            validators={[VALIDATOR_MINLENGTH(5)]}
            value={formState.description.value}
            valid={formState.description.isValid}
          />
          <Input
            id="address"
            name="Address"
            placeholder="address"
            icon="fa-map-marked-alt"
            onInput={inputHandler}
            validators={[VALIDATOR_REQUIRE()]}
            value={formState.address.value}
            valid={formState.address.isValid}
          />
          <div className="control">
            <button className="button is-primary" disabled={true}>
              Update
            </button>
          </div>
        </>
      ) : (
        <p>error, could not find the place</p>
      )}
    </Layout>
  );
};

export default editPlace;
