import React from "react";
import Layout from "../../components/Layout";
import Input from "../../components/Input";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from "../../utils/validators";
import { useRouter } from "next/router";
import { places } from "../../data";

const editPlace = () =>{
  const router = useRouter();
  const findPlace = places.find(
    (place) => place.id === router.query.editPlace
  );
  console.log(findPlace)
  return (
    <Layout title="Edit Place" subtitle="form to update an existing place">
      {!findPlace && <p>error, could not find the place</p>}
      <Input
        id="title"
        name="Title"
        placeholder="Name of the place"
        icon="fa-landmark"
        onInput={()=>{}}
        validators={[VALIDATOR_REQUIRE()]}
        value={findPlace.title}
        valid={true}
      />
            <Input
        id="description"
        name="Description"
        type="textarea"
        placeholder="About the place"
        onInput={()=>{}}
        validators={[VALIDATOR_MINLENGTH(5)]}
        value={findPlace.description}
        valid={true}
      />
      <Input
        id="address"
        name="Address"
        placeholder="address"
        icon="fa-map-marked-alt"
        onInput={()=>{}}
        validators={[VALIDATOR_REQUIRE()]}
        value={findPlace.address}
        valid={true}
      />
      <div className="control">
        <button className="button is-primary" disabled={true}>
          Update
        </button>
      </div>
    </Layout>
  );
}

export default editPlace;
