import React from 'react'

const Map = ({center, zoom}) => {
  const mapRef = React.useRef()

  React.useEffect(()=>{
    if(window !== undefined){
      const map = new window.google.maps.Map(mapRef.current,{
        center,
        zoom
      })
      new window.google.maps.Marker({position: center, map})
    }
  },[center, zoom])

  return (
    <div>
      map
    </div>
  )
}

export default Map
