import React from "react";

const Input = ({ type = "text", placeholder = "", name, icon = null }) => {

  const inputReducer = (state, action) => {
    switch(action.type) {
      case 'CHANGE':
        return {
          ...state,
          value: action.val,
          isValid: true
        };
      default:
        return state
    }
  }

  const [inputState, dispatch]=React.useReducer(inputReducer, {value:'', isValid: false})

  const handleChange = e => {
dispatch({type: 'CHANGE', val: e.target.value})
  }

  return (
    <div class="field">
      <label className="label">{name}</label>
      <div className={`control ${icon && 'has-icons-left'}`}>
        {type === "textarea" ? (
          <textarea className="textarea" placeholder={placeholder} onChange={handleChange} value={inputState.value}/>
        ) : 
          <input 
          className='input'
          type={type} 
          placeholder={placeholder} 
          onChange={handleChange} 
          value={inputState.value}
          />

        }
          <span className="icon is-small is-left">
            <i className={`fas ${icon}`}></i>
          </span>
        )}
      </div>
      {inputState.isValid && <p className="help is-danger">This {name} is invalid</p>}
    </div>
  );
};

export default Input;
