import React from 'react'
import Layout from "../components/Layout";
import Input from '../components/Input'
import {VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH} from '../utils/validators'

function addPlace() {

  const formReducer = (state, action) => {
    switch (action.type) {
      case 'INPUT_CHANGE':
        let formIsValid = true;
        for (const inputId in state.inputs) {
          if (inputId === action.inputId) {
            formIsValid = formIsValid && action.isValid;
          } else {
            formIsValid = formIsValid && state.inputs[inputId].isValid;
          }
        }
        return {
          ...state,
          inputs: {
            ...state.inputs,
            [action.inputId]: { value: action.value, isValid: action.isValid }
          },
          isValid: formIsValid
        };
      default:
        return state;
    }
  };
  const [formState, dispatch] = React.useReducer(formReducer, {
    inputs: {
      title: {
        value: '',
        isValid: false
      },
      description: {
        value: '',
        isValid: false
      }
    },
    isValid: false
  });
  const inputHandler = React.useCallback((id, value, isValid) => {
    dispatch({
      type: 'INPUT_CHANGE',
      value: value,
      isValid: isValid,
      inputId: id
    });
  }, []);
  return (
    <Layout title="Add Place" subtitle="form to add a new place">
      <Input name='Title'  placeholder="Name of the place" icon='fa-landmark' onInput={inputHandler} validators={[VALIDATOR_REQUIRE()]}/>
      <Input name='Description' type='textarea' placeholder="About the place" onInput={inputHandler} validators={[VALIDATOR_MINLENGTH(5)]}/>
      <Input name='Address'  placeholder="address" icon='fa-map-marked-alt' onInput={inputHandler} validators={[VALIDATOR_REQUIRE()]}/>
      <div className="control">
  <button className="button is-primary" disabled={!formState.isValid} >Submit</button>
</div>
    </Layout>
  )
}

export default addPlace
