import React from "react";
import {validate} from '../utils/validotors'

const Input = ({ type = "text", placeholder = "", name, icon = null, validators }) => {
  const inputReducer = (state, action) => {
    switch (action.type) {
      case "CHANGE":
        return {
          ...state,
          value: action.val,
          isValid: validate(action.val, action.validators),
        };
      default:
        return state;
    }
  };

  const [inputState, dispatch] = React.useReducer(inputReducer, {
    value: "",
    isValid: false,
  });

  const handleChange = (e) => {
    dispatch({ type: "CHANGE", val: e.target.value, validators });
  };

  return (
    <div className="field">
      <label className="label">{name}</label>
      <div className={`control ${icon && "has-icons-left"}`}>
        {type === "textarea" ? (
          <textarea
            className={`textarea ${!inputState.isValid && 'is-danger'}`}
            placeholder={placeholder}
            onChange={handleChange}
            value={inputState.value}
          ></textarea>
        ) : (
          <input
            className={`input ${!inputState.isValid && 'is-danger'}`}
            type={type}
            placeholder={placeholder}
            onChange={handleChange}
            value={inputState.value}
          />
        )}
        {icon && (
          <span className="icon is-small is-left">
            <i className={`fas ${icon}`}></i>
          </span>
        )}
      </div>
      {!inputState.isValid && (
        <p className="help is-danger">This {name} is invalid</p>
      )}
    </div>
  );
};

export default Input;
